query MovieReviewsListPaginationQuery(
  $count: Int = 5
  $cursor: String
  $id: ID!
) {
  node(id: $id) {
    __typename
    ...MovieReviewsListFragment_1G22uz
    id
  }
}

fragment MovieReviewFragment on Review {
  quote
  fresh
  criticName
  criticSource
}

fragment MovieReviewsListFragment_1G22uz on Movie {
  reviews(first: $count, after: $cursor) {
    edges {
      node {
        id
        ...MovieReviewFragment
        __typename
      }
      cursor
    }
    pageInfo {
      endCursor
      hasNextPage
    }
  }
  id
}

