query MovieQuery(
  $slug: String!
) {
  movie(slug: $slug) {
    title
    ...MovieDetailFragment
    ...MovieReviewsListFragment @defer(label: "MovieQuery$defer$MovieReviewsListFragment")
    id
  }
}

fragment LikeButtonFragment on Movie {
  id
  likedByViewer
}

fragment MovieDetailFragment on Movie {
  title
  criticScore
  audienceScore
  criticsConsensus
  imgUrl
  ...LikeButtonFragment @defer(label: "MovieDetailFragment$defer$LikeButtonFragment")
}

fragment MovieReviewFragment on Review {
  quote
  fresh
  criticName
  criticSource
}

fragment MovieReviewsListFragment on Movie {
  reviews(first: 5) {
    edges {
      node {
        id
        ...MovieReviewFragment
        __typename
      }
      cursor
    }
    pageInfo {
      endCursor
      hasNextPage
    }
  }
  id
}

